
public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// Difference between while and do-while loops
		boolean whileLoop = false;
		while (whileLoop) {
			System.out.println("there" + whileLoop);  //checks if boolean is true then does something.
		}
		
		int yoda = 0;  //does something first then asks questions
		do {
			System.out.println("My name is yoda");
			yoda++;
		} while (yoda < 5);
			
		}
		
		
	}


