
public class MultiTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		// Multiplication Table
		int rows = 6, cols = 4;
		for (int r = 1; r <= rows; r++) {
			for (int c=1; c <= cols; c++) {
				System.out.print((r*c) + "\t");
				
			}
			System.out.println();
		}
		
		
	}

}
