
public class isEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String isEven = args[0];
				
		if(Integer.parseInt(isEven) == 0) {
			System.out.println("The number " + isEven + " is even");
		}else System.out.println("The number " + isEven + " is odd");
		
		
		
	}

}
